mapper.insert:

INSERT INTO members (
		mb_id
	  , mb_pw
	  , mb_name
	  , mb_genre
	  , mb_profile
	  , mb_joindate
	  , mb_type
	   ) VALUES(
	    #{mb_id}
	  , CONCAT('*', UPPER(SHA1(UNHEX(SHA1(#{mb_pw})))))
	  , #{mb_name}
	  , #{mb_genre}
	  , #{mb_profile}
	  , DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i')
	  , 'N'
 	)

mapper.update:

 UPDATE members
  SET mb_pw = CONCAT('*', UPPER(SHA1(UNHEX(SHA1(#{mb_pw})))))
	, mb_genre = #{mb_genre}
	, mb_profile  = #{mb_profile}
WHERE mb_id = #{mb_id}
