mapper.update:

update movies set 
movie_cnt = movie_cnt+1 
where movie_seq = #{movie_seq}

mapper.select:
SELECT DISTINCT Hs.movie_seq as movie_seq , date_format(Hs.history_date, '%Y-%m-%d') as history_date , Mv.movie_title as movie_title , Mv.movie_img as movie_img FROM history as Hs LEFT JOIN movies as Mv ON Hs.movie_seq = Mv.movie_seq
where Hs.mb_id = #{mb_id}
group by Hs.movie_seq,date_format(Hs.history_date, '%Y-%m-%d'),Mv.movie_title order by date_format(Hs.history_date, '%Y-%m-%d') desc limit 4

mapper.insert:

INSERT INTO history (
		mb_id
	  , movie_seq
	  , history_date
	   ) VALUES(
	    #{mb_id}
	  , #{movie_seq}
	  , DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
 	)
